import{_ as s,k as a,a6 as l,j as o}from"./chunks/framework.Bul4SA1j.js";const k=JSON.parse('{"title":"v1.31.0","description":"The changelog for v1.31.0 (Update Name Placeholder).","frontmatter":{"title":"v1.31.0","page_title":"v1.31.0 (Update Name Placeholder) Changelog","description":"The changelog for v1.31.0 (Update Name Placeholder).","category":"Releases","mentions":["Andexter8"]},"headers":[{"level":2,"title":"Changes","slug":"changes","link":"#changes","children":[{"level":3,"title":"Anti-Spam","slug":"anti-spam","link":"#anti-spam","children":[]},{"level":3,"title":"Commands","slug":"commands","link":"#commands","children":[]},{"level":3,"title":"Command Parameter Types","slug":"command-parameter-types","link":"#command-parameter-types","children":[{"level":4,"title":"BlockMask","slug":"blockmask","link":"#blockmask","children":[]}]}]}],"relativePath":"changelogs/v1.31.0.md","filePath":"changelogs/v1.31.0.md"}'),i={name:"changelogs/v1.31.0.md"};function c(n,e,t,d,p,r){return o(),a("div",null,e[0]||(e[0]=[l('<h2 id="changes" tabindex="-1">Changes <a class="header-anchor" href="#changes" aria-label="Permalink to &quot;Changes&quot;">​</a></h2><h3 id="anti-spam" tabindex="-1">Anti-Spam <a class="header-anchor" href="#anti-spam" aria-label="Permalink to &quot;Anti-Spam&quot;">​</a></h3><ul><li>The anti-spam system now tells players how much longer it is until they get unmuted.</li></ul><h3 id="commands" tabindex="-1">Commands <a class="header-anchor" href="#commands" aria-label="Permalink to &quot;Commands&quot;">​</a></h3><ul><li>Massive updates to the <code class="shiki"><span>\\\\regenerateblocks</span></code> command.</li></ul><h3 id="command-parameter-types" tabindex="-1">Command Parameter Types <a class="header-anchor" href="#command-parameter-types" aria-label="Permalink to &quot;Command Parameter Types&quot;">​</a></h3><h4 id="blockmask" tabindex="-1">BlockMask <a class="header-anchor" href="#blockmask" aria-label="Permalink to &quot;BlockMask&quot;">​</a></h4><ul><li>Added a new type to block masks: presets! You can specify these presets just like you would specify a regular block type. When you specify a preset, then it will match any of the block types that it includes. Here is a list of the currently supported presets: <ul><li><code class="shiki"><span>preset:leaves</span></code>: includes all block types that have &quot;leaves&quot; in the name.</li><li><code class="shiki"><span>preset:deforest</span></code>: includes all block types that have &quot;leaves&quot; or &quot;sapling&quot; in the name, any block with the &quot;log&quot; or &quot;plant&quot; tags, <code class="shiki"><span>short_grass</span></code>, <code class="shiki"><span>tall_grass</span></code>, <code class="shiki"><span>vine</span></code>, <code class="shiki"><span>dandelion</span></code>, <code class="shiki"><span>allium</span></code>, <code class="shiki"><span>brown_mushroom_block</span></code>, <code class="shiki"><span>red_mushroom_block</span></code>, <code class="shiki"><span>mushroom_stem</span></code>, <code class="shiki"><span>crimson_roots</span></code>, <code class="shiki"><span>warped_roots</span></code>, and <code class="shiki"><span>bee_nest</span></code>. It is still a work in progress and will include more block types in the future.</li><li><code class="shiki"><span>preset:ores</span></code>: includes all block types that have &quot;ore&quot; in the name, as well as ancient debris.</li><li><code class="shiki"><span>preset:ore_blocks</span></code>: includes the following block types: <ul><li><code class="shiki"><span>coal_block</span></code></li><li><code class="shiki"><span>copper_block</span></code></li><li><code class="shiki"><span>exposed_copper</span></code></li><li><code class="shiki"><span>weathered_copper</span></code></li><li><code class="shiki"><span>oxidized_copper</span></code></li><li><code class="shiki"><span>waxed_copper</span></code></li><li><code class="shiki"><span>waxed_exposed_copper</span></code></li><li><code class="shiki"><span>waxed_weathered_copper</span></code></li><li><code class="shiki"><span>waxed_oxidized_copper</span></code></li><li><code class="shiki"><span>iron_block</span></code></li><li><code class="shiki"><span>gold_block</span></code></li><li><code class="shiki"><span>emerald_block</span></code></li><li><code class="shiki"><span>diamond_block</span></code></li><li><code class="shiki"><span>netherite_block</span></code></li><li><code class="shiki"><span>redstone_block</span></code></li><li><code class="shiki"><span>lapis_block</span></code></li><li><code class="shiki"><span>raw_copper_block</span></code></li><li><code class="shiki"><span>raw_iron_block</span></code></li><li><code class="shiki"><span>raw_gold_block</span></code></li></ul></li><li><code class="shiki"><span>preset:liquid</span></code>: includes <code class="shiki"><span>water</span></code>, <code class="shiki"><span>flowing_water</span></code>, <code class="shiki"><span>lava</span></code>, and <code class="shiki"><span>flowing_lava</span></code>.</li></ul></li><li>Added the ability to use block tags in the block masks. To use a block tag, just put <code class="shiki"><span>tag:tagname</span></code> as the block type. ex. <code class="shiki"><span>tag:plant</span></code> or <code class="shiki"><span>tag:minecraft:is_hoe_item_destructable</span></code>.</li></ul>',8)]))}const m=s(i,[["render",c]]);export{k as __pageData,m as default};
